import runtime from './runtime';
import include from './adapter/include';
import artRule from './adapter/rule.art';
import nativeRule from './adapter/rule.native';
/* 模板编译器默认配置 */
const settings = {
    // 是否启用模板 debug
    debug: false,
    // 模板路径 id or url
    path: '',
    // 模板内容
    source: '',
    // 模板语法规则列表
    rules: [nativeRule, artRule],
    // 是否开启对模板输出语句自动编码功能。为 false 则关闭编码输出功能
    // escape 可以防范 XSS 攻击
    escape: true,
    // 子模板编译适配器
    include,
    // 忽略的变量。被模板编译器忽略的模板变量列表
    ignore: [],
    // 子模版
    sublist: {},
    // 编译出错时是否重试
    recompile: false,
    // 导入的模板变量
    imports: runtime
};
class Defaults {
    settings;
    $extend;
    /** 模板编译器默认配置 */
    constructor(settings) {
        this.settings = settings;
        this.$extend = function (options) {
            options = options || {};
            return Object.assign({}, settings, options);
        };
    }
}
export default new Defaults(settings);
