/**
 * 16 进制字符串与普通字符串之间的简单转换
 */
class Hexs {
    textEncoder;
    textDecoder;
    constructor() {
        this.textEncoder = new TextEncoder();
        this.textDecoder = new TextDecoder();
    }
    /**
     * 将目标 16 进制的字符串, 转换成普通字符串
     * @param str 目标 16 进制字符串
     * @returns 转换后的普通字符串
     */
    hexToStr(str) {
        const hexs = str.match(/\w{2}/g)?.map(hex => parseInt(hex, 16));
        if (hexs?.length) {
            return this.textDecoder.decode(Uint8Array.from(hexs));
        }
        return '';
    }
    /**
     * 将目标普通的字符串转换成 16 进制字符串
     * @param str 目标普通的字符串
     * @returns 转换后的16进制字符串
     */
    strToHex(str) {
        if (str.length) {
            return Array.from(this.textEncoder.encode(str))
                .flatMap(hex => hex.toString(16))
                .join('')
                .toUpperCase();
        }
        return '';
    }
}
export default new Hexs();
