/**
 * 处理了 url 相关工具集
 */
class Urls {
    constructor() { }
    /**
     * 获取的目标查询参数的值
     * @param key 目标参数
     * @returns 目标参数的值
     */
    parameter(key) {
        const reg = new RegExp('[?|&]' + key + '=([^&]+)'), match = location.search.match(reg);
        return match && match[1];
    }
}
export default new Urls();
